import { Link, useLocation } from "wouter";
import { Bell, LogOut } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { useAuth } from "@/hooks/useAuth";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

export default function Header() {
  const [location] = useLocation();
  const { user, isAdmin } = useAuth();

  const navigation = [
    { name: "Dashboard", href: "/" },
    { name: "My Courses", href: "/courses" },
    { name: "Test Results", href: "/test-results" },
    ...(isAdmin ? [{ name: "Admin Panel", href: "/admin" }] : []),
  ];

  const isActive = (href: string) => {
    if (href === "/" && location === "/") return true;
    if (href !== "/" && location.startsWith(href)) return true;
    return false;
  };

  const handleLogout = () => {
    const token = localStorage.getItem("token");
    if (token) {
      // Email/password logout
      localStorage.removeItem("token");
      window.location.href = '/';
    } else {
      // Replit logout
      window.location.href = '/api/logout';
    }
  };

  return (
    <header className="bg-white shadow-sm border-b border-gray-200 sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <div className="flex items-center space-x-4">
            <div className="flex-shrink-0">
              <Link href="/">
                <h1 className="text-2xl font-bold text-primary cursor-pointer">
                  EduPlatform
                </h1>
              </Link>
            </div>
            <nav className="hidden md:flex space-x-8">
              {navigation.map((item) => (
                <Link key={item.name} href={item.href}>
                  <span
                    className={`px-1 pb-4 pt-5 text-sm font-medium border-b-2 transition-colors duration-200 cursor-pointer ${
                      isActive(item.href)
                        ? "text-primary border-primary"
                        : "text-gray-500 hover:text-gray-700 border-transparent"
                    }`}
                  >
                    {item.name}
                  </span>
                </Link>
              ))}
            </nav>
          </div>
          <div className="flex items-center space-x-4">
            <Button variant="ghost" size="icon">
              <Bell className="h-5 w-5 text-gray-400" />
            </Button>
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <div className="flex items-center space-x-3 cursor-pointer">
                  <Avatar className="h-8 w-8">
                    <AvatarImage src={user?.profileImageUrl} />
                    <AvatarFallback>
                      {user?.firstName?.[0]}{user?.lastName?.[0]}
                    </AvatarFallback>
                  </Avatar>
                  <span className="text-sm font-medium text-gray-700">
                    {user?.firstName} {user?.lastName}
                  </span>
                </div>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                <DropdownMenuItem onClick={handleLogout}>
                  <LogOut className="mr-2 h-4 w-4" />
                  Sign Out
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </div>
      </div>
    </header>
  );
}

client/src/components/pdf-viewer.tsx.content:
import { Button } from "@/components/ui/button";
import { Eye, Download, FileText, ExternalLink } from "lucide-react";

interface PdfViewerProps {
  notes: any[]; // MongoDB note structure
}

export default function PdfViewer({ notes }: PdfViewerProps) {
  const handleView = (note: any) => {
    // Open PDF in new tab for viewing
    window.open(note.pdfUrl, '_blank');
  };

  const handleDownload = (note: any) => {
    // Create a temporary link to download the PDF
    const link = document.createElement('a');
    link.href = note.pdfUrl;
    link.download = `${note.title}.pdf`;
    link.target = '_blank';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="bg-gray-50 rounded-lg p-6">
      <div className="space-y-3">
        {notes.map((note, index) => (
          <div key={note._id || index} className="flex items-center justify-between p-4 bg-white rounded-lg border shadow-sm">
            <div className="flex items-center space-x-3">
              <FileText className="h-6 w-6 text-red-500" />
              <div>
                <p className="font-medium text-gray-900">{note.title}</p>
                <p className="text-sm text-gray-500">
                  PDF Document â€¢ {note.fileSize || 'Unknown size'}
                </p>
              </div>
            </div>
            <div className="flex space-x-2">
              <Button variant="outline" size="sm" onClick={() => handleView(note)}>
                <Eye className="h-4 w-4 mr-1" />
                View
              </Button>
              <Button variant="outline" size="sm" onClick={() => handleDownload(note)}>
                <Download className="h-4 w-4 mr-1" />
                Download
              </Button>
              <Button 
                variant="ghost" 
                size="sm"
                onClick={() => window.open(note.pdfUrl, '_blank')}
              >
                <ExternalLink className="h-4 w-4" />
              </Button>
            </div>
          </div>
        ))}
        {notes.length === 0 && (
          <div className="text-center py-8 text-gray-500 bg-white rounded-lg border">
            <FileText className="h-12 w-12 mx-auto mb-4 opacity-50" />
            <p>No PDF notes available for this course</p>
            <p className="text-xs mt-1">PDF resources will appear here when added by the instructor</p>
          </div>
        )}
      </div>
    </div>
  );
}

